<script>
export default {
    name: 'leaderBar',
    data() {
        return {
            glableMask: 'glableMask',
            buttonItems: [
                'left: 0; top: 0;',
                'left: .1rem; top: 0;',
                'left: .2rem; top: 0;',
                'left: 0; top: .1rem;',
                'left: .1rem; top: .1rem;',
                'left: .2rem; top: .1rem;',
                'left: 0; top: .2rem;',
                'left: .1rem; top: .2rem;',
                'left: .2rem; top: .2rem;'
            ],
            buttonItemsUnclick: [
                'left: 0; top: 0;',
                'left: .1rem; top: 0;',
                'left: .2rem; top: 0;',
                'left: 0; top: .1rem;',
                'left: .1rem; top: .1rem;',
                'left: .2rem; top: .1rem;',
                'left: 0; top: .2rem;',
                'left: .1rem; top: .2rem;',
                'left: .2rem; top: .2rem;'
            ],
            buttonItemsClick: [
                'left: 0; top: 0; transform: translate(5px, 5px);',
                'left: .1rem; top: 0; transform: translate(0px, 5px);',
                'left: .2rem; top: 0; transform: translate(-5px, 5px);',
                'left: 0; top: .1rem; transform: translate(5px, 0px);',
                'left: .1rem; top: .1rem; transform: translate(0px, 0px);',
                'left: .2rem; top: .1rem; transform: translate(-5px, 0px);',
                'left: 0; top: .2rem; transform: translate(5px, -5px);',
                'left: .1rem; top: .2rem; transform: translate(0px, -5px);',
                'left: .2rem; top: .2rem; transform: translate(-5px, -5px);'
            ],
            maskShow: false
        }
    },
    methods: {
        buttonChange() {
            this.maskShow = true
            this.glableMask = 'glableMask enter-active'
            this.buttonItems = this.buttonItemsClick
        },
        maskChange() {
            this.glableMask = 'glableMask leave-active'
            this.buttonItems = this.buttonItemsUnclick
            setTimeout(() => {
                this.maskShow = false
            }, 200)
        }
    }
}
</script>

