<style>
.enter-active {
    -webkit-animation: enter 0.3s ease-in-out;
    -moz-animation: enter 0.3s ease-in-out;
    -o-animation: enter 0.3s ease-in-out;
    animation: enter 0.3s ease-in-out;
}
.leave-active {
    -webkit-animation: leave 0.3s ease-in-out;
    -moz-animation: leave 0.3s ease-in-out;
    -o-animation: leave 0.3s ease-in-out;
    animation: leave 0.3s ease-in-out;
}
@keyframes enter {
    from {
        opacity: 0;
        -webkit-transform: scale(1.35);
        -moz-transform: scale(1.35);
        -ms-transform: scale(1.35);
        -o-transform: scale(1.35);
        transform: scale(1.35);
    }
    to {
        opacity: 1;
        -webkit-transform: scale(1);
        -moz-transform: scale(1);
        -ms-transform: scale(1);
        -o-transform: scale(1);
        transform: scale(1);
    }
}
@keyframes leave {
    from {
        opacity: 1;
        -webkit-transform: scale(1);
        -moz-transform: scale(1);
        -ms-transform: scale(1);
        -o-transform: scale(1);
        transform: scale(1);
    }
    to {
        opacity: 0;
        -webkit-transform: scale(1.35);
        -moz-transform: scale(1.35);
        -ms-transform: scale(1.35);
        -o-transform: scale(1.35);
        transform: scale(1.35);
    }
}
</style>
